import React, { useEffect, useState } from 'react';
import {
  Bot,
  Play,
  Square,
  Trash2,
  PlusCircle,
  TrendingUp,
  TrendingDown,
  Activity,
  DollarSign,
  BarChart3,
  Settings,
  X,
  RefreshCw
} from 'lucide-react';

import api from '../services/api'; // ajuste conforme sua estrutura

export default function TradingBotsPage() {
  const [bots, setBots] = useState([]);
  const [selectedBot, setSelectedBot] = useState(null);
  const [botTrades, setBotTrades] = useState([]);
  const [showCreateModal, setShowCreateModal] = useState(false);
  const [newBot, setNewBot] = useState({
    name: '',
    strategyType: '',
    pairSymbol: '',
    investmentAmount: '',
  });
  const [isLoading, setIsLoading] = useState(false);
  const [isRefreshing, setIsRefreshing] = useState(false);

  // === Buscar bots e trades periodicamente ===
  useEffect(() => {
    fetchBots();

    const interval = setInterval(() => {
      console.log('ðŸ”„ Auto-refresh: Atualizando bots...');
      fetchBots();

      if (selectedBot) {
        console.log('ðŸ”„ Auto-refresh: Atualizando trades do bot', selectedBot.id);
        fetchBotTrades(selectedBot.id);
      }
    }, 60000); // a cada 60 segundos

    return () => clearInterval(interval);
  }, [selectedBot]);

  // === Buscar bots ===
  const fetchBots = async () => {
    try {
      setIsLoading(true);
      const response = await api.get('/bots');
      setBots(response.data);
    } catch (error) {
      console.error('Erro ao buscar bots:', error);
    } finally {
      setIsLoading(false);
    }
  };

  // === Buscar trades do bot selecionado ===
  const fetchBotTrades = async (botId) => {
    try {
      const response = await api.get(`/bots/${botId}/trades`);
      setBotTrades(response.data);
    } catch (error) {
      console.error('Erro ao buscar trades do bot:', error);
    }
  };

  // === Atualizar manualmente ===
  const handleRefresh = async () => {
    setIsRefreshing(true);
    await fetchBots();

    if (selectedBot) {
      await fetchBotTrades(selectedBot.id);
    }

    setTimeout(() => setIsRefreshing(false), 500);
  };

  // === Criar novo bot ===
  const handleCreateBot = async () => {
    try {
      await api.post('/bots', newBot);
      setShowCreateModal(false);
      resetForm();
      fetchBots();
    } catch (error) {
      console.error('Erro ao criar bot:', error);
    }
  };

  const resetForm = () => {
    setNewBot({
      name: '',
      strategyType: '',
      pairSymbol: '',
      investmentAmount: '',
    });
  };

  // === Iniciar bot ===
  const handleStartBot = async (id) => {
    try {
      await api.post(`/bots/${id}/start`);
      fetchBots();
    } catch (error) {
      console.error('Erro ao iniciar bot:', error);
    }
  };

  // === Parar bot ===
  const handleStopBot = async (id) => {
    try {
      await api.post(`/bots/${id}/stop`);
      fetchBots();
    } catch (error) {
      console.error('Erro ao parar bot:', error);
    }
  };

  // === Deletar bot ===
  const handleDeleteBot = async (id) => {
    if (!window.confirm('Tem certeza que deseja deletar este bot?')) return;
    try {
      await api.delete(`/bots/${id}`);
      fetchBots();
    } catch (error) {
      console.error('Erro ao deletar bot:', error);
    }
  };

  return (
    <div style={styles.container}>
      <h1 style={styles.title}><Bot size={28} /> Painel de Bots</h1>

      <div style={styles.buttonGroup}>
        {/* BotÃ£o de atualizaÃ§Ã£o manual */}
        <button
          onClick={handleRefresh}
          disabled={isRefreshing}
          style={{
            ...styles.button,
            background: '#667eea',
            color: 'white',
            opacity: isRefreshing ? 0.5 : 1
          }}
        >
          <RefreshCw
            size={20}
            style={{ animation: isRefreshing ? 'spin 1s linear infinite' : 'none' }}
          />
          Atualizar
        </button>

        {/* BotÃ£o Criar Bot */}
        <button
          onClick={() => setShowCreateModal(true)}
          style={{ ...styles.button, ...styles.primaryButton }}
        >
          <PlusCircle size={20} />
          Criar Bot
        </button>
      </div>

      {isLoading ? (
        <p style={styles.loading}>Carregando bots...</p>
      ) : (
        <div style={styles.grid}>
          {bots.map((bot) => (
            <div key={bot.id} style={styles.card}>
              <div style={styles.cardHeader}>
                <h3>{bot.name}</h3>
                <span style={styles.strategy}>{bot.strategyType}</span>
              </div>
              <p><DollarSign size={14} /> Par: {bot.pairSymbol}</p>
              <p><BarChart3 size={14} /> Investimento: ${bot.investmentAmount}</p>
              <p>
                <Activity size={14} /> Status:{' '}
                <strong style={{ color: bot.active ? '#4caf50' : '#ff5252' }}>
                  {bot.active ? 'Ativo' : 'Parado'}
                </strong>
              </p>

              <div style={styles.actions}>
                {bot.active ? (
                  <button onClick={() => handleStopBot(bot.id)} style={{ ...styles.actionBtn, background: '#ff5252' }}>
                    <Square size={16} /> Parar
                  </button>
                ) : (
                  <button onClick={() => handleStartBot(bot.id)} style={{ ...styles.actionBtn, background: '#4caf50' }}>
                    <Play size={16} /> Iniciar
                  </button>
                )}
                <button onClick={() => handleDeleteBot(bot.id)} style={{ ...styles.actionBtn, background: '#6c757d' }}>
                  <Trash2 size={16} /> Excluir
                </button>
                <button onClick={() => { setSelectedBot(bot); fetchBotTrades(bot.id); }} style={{ ...styles.actionBtn, background: '#667eea' }}>
                  <Settings size={16} /> Detalhes
                </button>
              </div>
            </div>
          ))}
        </div>
      )}

      {/* Modal de CriaÃ§Ã£o */}
      {showCreateModal && (
        <div style={styles.modal} onClick={() => setShowCreateModal(false)}>
          <div style={styles.modalContent} onClick={(e) => e.stopPropagation()}>
            <h2>Criar Novo Bot</h2>

            <input
              type="text"
              placeholder="Nome do bot"
              value={newBot.name}
              onChange={(e) => setNewBot({ ...newBot, name: e.target.value })}
              style={styles.input}
            />
            <input
              type="text"
              placeholder="EstratÃ©gia (ex: GRID, DCA, STOP_TAKE)"
              value={newBot.strategyType}
              onChange={(e) => setNewBot({ ...newBot, strategyType: e.target.value })}
              style={styles.input}
            />
            <input
              type="text"
              placeholder="SÃ­mbolo (ex: BTCUSDT)"
              value={newBot.pairSymbol}
              onChange={(e) => setNewBot({ ...newBot, pairSymbol: e.target.value })}
              style={styles.input}
            />
            <input
              type="number"
              placeholder="Valor investido"
              value={newBot.investmentAmount}
              onChange={(e) => setNewBot({ ...newBot, investmentAmount: e.target.value })}
              style={styles.input}
            />

            <button onClick={handleCreateBot} style={styles.primaryButton}>
              Criar
            </button>
          </div>
        </div>
      )}

      {/* Modal de Detalhes */}
      {selectedBot && (
        <div style={styles.modal} onClick={() => setSelectedBot(null)}>
          <div
            style={{ ...styles.modalContent, maxWidth: '800px' }}
            onClick={(e) => e.stopPropagation()}
          >
            <div style={{
              display: 'flex',
              justifyContent: 'space-between',
              alignItems: 'center',
              marginBottom: '25px'
            }}>
              <h2 style={{ margin: 0, fontSize: '24px', fontWeight: 'bold' }}>
                {selectedBot.name}
              </h2>

              <div style={{ display: 'flex', gap: '10px', alignItems: 'center' }}>
                <button
                  onClick={handleRefresh}
                  disabled={isRefreshing}
                  style={{
                    padding: '10px',
                    background: isRefreshing ? '#ccc' : '#667eea',
                    color: 'white',
                    border: 'none',
                    borderRadius: '10px',
                    cursor: isRefreshing ? 'not-allowed' : 'pointer',
                    display: 'flex',
                    alignItems: 'center'
                  }}
                >
                  <RefreshCw
                    size={18}
                    style={{ animation: isRefreshing ? 'spin 1s linear infinite' : 'none' }}
                  />
                </button>

                <button
                  onClick={() => setSelectedBot(null)}
                  style={{
                    background: 'none',
                    border: 'none',
                    cursor: 'pointer',
                    color: '#999'
                  }}
                >
                  <X size={24} />
                </button>
              </div>
            </div>

            <h3>HistÃ³rico de Trades</h3>
            {botTrades.length === 0 ? (
              <p>Nenhuma trade registrada.</p>
            ) : (
              <ul style={styles.tradeList}>
                {botTrades.map((trade) => (
                  <li key={trade.id} style={styles.tradeItem}>
                    <span>{trade.side === 'BUY' ? <TrendingUp color="#4caf50" /> : <TrendingDown color="#ff5252" />} {trade.side}</span>
                    <span>PreÃ§o: ${trade.price}</span>
                    <span>Qtd: {trade.quantity}</span>
                    <span>Lucro: {trade.profit ? trade.profit.toFixed(2) : 0}</span>
                  </li>
                ))}
              </ul>
            )}
          </div>
        </div>
      )}

      <style>{`
        @keyframes spin {
          from { transform: rotate(0deg); }
          to { transform: rotate(360deg); }
        }
      `}</style>
    </div>
  );
}

const styles = {
  container: { padding: '30px', color: '#fff' },
  title: { display: 'flex', alignItems: 'center', gap: '10px' },
  buttonGroup: { display: 'flex', gap: '10px', marginBottom: '20px' },
  button: { display: 'flex', alignItems: 'center', gap: '8px', padding: '10px 16px', borderRadius: '10px', border: 'none', cursor: 'pointer', fontWeight: 'bold' },
  primaryButton: { background: '#4caf50', color: 'white' },
  loading: { textAlign: 'center' },
  grid: { display: 'grid', gridTemplateColumns: 'repeat(auto-fill, minmax(250px, 1fr))', gap: '20px' },
  card: { background: '#1e1e2f', padding: '20px', borderRadius: '12px', boxShadow: '0 2px 8px rgba(0,0,0,0.3)' },
  cardHeader: { display: 'flex', justifyContent: 'space-between', alignItems: 'center' },
  strategy: { background: '#667eea', padding: '4px 10px', borderRadius: '6px', fontSize: '12px' },
  actions: { display: 'flex', gap: '8px', marginTop: '10px', flexWrap: 'wrap' },
  actionBtn: { display: 'flex', alignItems: 'center', gap: '6px', color: '#fff', border: 'none', padding: '8px 12px', borderRadius: '8px', cursor: 'pointer' },
  modal: { position: 'fixed', top: 0, left: 0, width: '100%', height: '100%', background: 'rgba(0,0,0,0.6)', display: 'flex', justifyContent: 'center', alignItems: 'center' },
  modalContent: { background: '#2b2b3c', padding: '30px', borderRadius: '12px', width: '400px' },
  input: { width: '100%', marginBottom: '10px', padding: '8px', borderRadius: '8px', border: '1px solid #ccc' },
  tradeList: { listStyle: 'none', padding: 0, marginTop: '10px' },
  tradeItem: { display: 'flex', justifyContent: 'space-between', padding: '6px 0', borderBottom: '1px solid #444' },
};
